      CREATE TABLE `season` (
      `seasonID` int(11) NOT NULL,
      `seasonName` varchar(255) NULL,
      PRIMARY KEY (`seasonID`) 
      );



      CREATE TABLE `sceneryType` (
      `typeID` int(11) NOT NULL AUTO_INCREMENT,
      `name` varchar(255) NULL,
      `description` varchar(255) NULL,
      PRIMARY KEY (`typeID`) 
      );
    



      CREATE TABLE `scenery` (
      `sceneryID` varchar(255) NOT NULL,
      `name` varchar(255) NULL,
      `province` varchar(255) NULL,
      `city` varchar(255) NULL,
      `area` varchar(255) NULL,
      `level` varchar(255) NULL,
      `quality` varchar(255) NULL,
      `description` text NULL,
      `website` varchar(512) NULL,
      `symbolImage` varchar(512) NULL,
      `openTime` varchar(255) NULL,
      `closeTime` varchar(255) NULL,
      `price` decimal(11,2) NULL,
      PRIMARY KEY (`sceneryID`) 
      );
    



      CREATE TABLE `typeRelation` (
      `sceneryID` varchar(255) NOT NULL,
      `typeID` int(11) NOT NULL,
      PRIMARY KEY (`sceneryID`, `typeID`) 
      );
    



      CREATE TABLE `sceneryImages` (
      `sceneryID` varchar(255) NOT NULL,
      `imageID` int(11) NOT NULL AUTO_INCREMENT,
      `imageUrl` varchar(255) NULL,
      `imageName` varchar(255) NULL,
      `imageDesc` varchar(255) NULL,
      `takenTime` timestamp NULL,
      PRIMARY KEY (`sceneryID`, `imageID`) 
      );
    



      CREATE TABLE `fitSeason` (
      `sceneryID` varchar(255) NOT NULL,
      `seasonID` int(11) NOT NULL,
      PRIMARY KEY (`sceneryID`, `seasonID`) 
      );
    



      ALTER TABLE `scenery` ADD CONSTRAINT `scenery-type` FOREIGN KEY (`sceneryID`) REFERENCES `typeRelation` (`sceneryID`);
      ALTER TABLE `sceneryType` ADD CONSTRAINT `type-relation` FOREIGN KEY (`typeID`) REFERENCES `typeRelation` (`typeID`);
      ALTER TABLE `scenery` ADD CONSTRAINT `scenery-images` FOREIGN KEY (`sceneryID`) REFERENCES `sceneryImages` (`sceneryID`);
      ALTER TABLE `scenery` ADD CONSTRAINT `scenery-season` FOREIGN KEY (`sceneryID`) REFERENCES `fitSeason` (`sceneryID`);
      ALTER TABLE `season` ADD CONSTRAINT `season-fit` FOREIGN KEY (`seasonID`) REFERENCES `fitSeason` (`seasonID`);
